    private void cadastrarAlunoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cadastrarAlunoActionPerformed
        // TODO add your handling code here:
        
        String cpf_al = cpf_a.getText();
        String nome_al = nome_a.getText();
        String sexo_al= sexo_a.getText();
        String idade_al= nasc_a.getText();
        String serie_al= serie_a.getText();
        String turno_al= turno_a.getText();
        String turma_al = turma_a.getText();
       
        objalu.cadastrarAluno1(stmt,cpf_al, nome_al, sexo_al, idade_al, serie_al, turno_al, turma_al);
        
        DefaultTableModel dtAluno =( DefaultTableModel) jtAluno.getModel();
        Object [] dados = {cpf_a.getText(),nome_a.getText(),
            sexo_a.getText(),nasc_a.getText(),serie_a.getText(), turno_a.getText(),turma_a.getText()};
        dtAluno.addRow(dados);
       
        
    }//GEN-LAST:event_cadastrarAlunoActionPerformed

