    private void atualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_atualizarActionPerformed
        // TODO add your handling code here:
          String cpfatualizar = Aluno_new.getText();

        if (button1.isSelected()){
            String turma = JOptionPane.showInputDialog("Insira a nova turma:");
            objalu.atualizarTurma(cpfatualizar, turma);
        }
            if(button2.isSelected()){
            String turno = JOptionPane.showInputDialog("Insira o novo turno:");
            objalu.atualizarTurno(cpfatualizar, turno);
            }else{
             if(button3.isSelected()){
                String serie = JOptionPane.showInputDialog("Insira a nova SÃ©rie:");
                objalu.atualizarSerie(cpfatualizar, serie);   
             }   
            }
        
                
                
    }//GEN-LAST:event_atualizarActionPerformed


