    private void salvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_salvarActionPerformed
        // TODO add your handling code here:
        String cod_nota = cod_n.getText();
        String freq_nota = freq_n.getText();
        String cod_disc= disc_n.getSelectedText();
        String cpf_prof= professor_n.getText();
        String cpf_al= aluno_n.getText();
        String nota_n= nota_no.getText();
      
        
        objnota.cadastrarNotas(stmt,cod_nota, freq_nota, cod_disc, cpf_prof, cpf_al,nota_n);
       
    }//GEN-LAST:event_salvarActionPerformed

