    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
         String notaatualizar = nota_new.getText();

        if (button1.isSelected()){
            String nota = JOptionPane.showInputDialog("Insira a nova nota:");
            objnota.atualizarNota(notaatualizar, nota);
        }
        if(button2.isSelected()){
              String freq = JOptionPane.showInputDialog("Insira a nova frequÃªncia:");
            objnota.atualizarFrequencia(notaatualizar, freq);
        }
    }//GEN-LAST:event_jButton1ActionPerformed


