    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String cpf_prof = cpf_p.getText();
        String nome_prof = nome_p.getText();
        String sexo_prof= sexo_p.getSelectedText();
        String idade_prof= idade_p.getText();
        String nome_disc= disc_p.getText();
        String salario_prof= salario_p.getText();
        
        objprof.cadastrarProfessor(stmt, cpf_prof, nome_prof, sexo_prof, idade_prof, nome_disc, salario_prof);
       
    }//GEN-LAST:event_jButton1ActionPerformed

