    private void atualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_atualizarActionPerformed
        // TODO add your handling code here:
         String cpfatualizar = salario_new.getText();

        if (button1.isSelected()){
            String salario = JOptionPane.showInputDialog("Insira o novo Salário:");
            objprof.atualizarSalario(cpfatualizar, salario);
        }else{
            JOptionPane.showMessageDialog(rootPane, "Opção inválida , digite o cpf do professor para atualizar");
        }
    }//GEN-LAST:event_atualizarActionPerformed


